import React, { useLayoutEffect, useState } from 'react'
import { Keyboard, KeyboardAvoidingView, Platform, StyleSheet, Text, TextInput, View } from 'react-native'
import { Avatar } from 'react-native-elements'
import { TouchableOpacity, TouchableWithoutFeedback } from 'react-native';
import {AntDesign, FontAwesome, Ionicons} from '@expo/vector-icons'
import { SafeAreaView } from 'react-native-safe-area-context';
import { StatusBar } from 'expo-status-bar';
import { ScrollView } from 'react-native-gesture-handler';
import {auth, db, firebase} from '../firbase';


const ChatScreen = ({navigation , route}) => {

    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([]);

    useLayoutEffect(() => {
        navigation.setOptions({
            title: 'Chat',
            headerBackTitle: false,
            headerTitleAlign: 'left',
            headerTitle: () => (
                <View style={{flexDirection:"row", alignItems: 'center'}}>
                    <Avatar rounded source={{uri: messages[0]?.data.photoURL,}} />
                    <Text
                    style={{color: "white", marginLeft: 10, fontWeight: "700"}}
                    >{route.params.chatName}</Text>
                </View>
            ),
            headerLeft: () => (
              <TouchableOpacity 
              onPress={navigation.goBack}
              style={{marginLeft: 10}}>
                <AntDesign name="arrowleft" size={24} color="white"/>
              </TouchableOpacity>
            ),
            headerRight: () => (
                <View
                style={{flexDirection:"row",
                        justifyContent: "space-between", 
                        width: 80, 
                        marginRight: 20}}>
                <TouchableOpacity>
                    <FontAwesome name="video-camera" size={24} color="white"/>
                </TouchableOpacity>
                <TouchableOpacity>
                    <Ionicons name="call" size={24} color="white"/>
                </TouchableOpacity>
                </View>
            )
        });
    },[navigation,messages]);

    const sendMessage = () => {
        Keyboard.dismiss();
            db.collection('chats').doc(route.params.id).collection('messages').add({
            timestamp: firebase.firestore.FieldValue.serverTimestamp(),
            message: input,
            displayName: auth.currentUser.displayName,
            email: auth.currentUser.email,
            photoURL: auth.currentUser.photoURL
        });
        setInput("");

    };

    useLayoutEffect(() => {
      const unsubscribe = db.collection("chats")
                          .doc(route.params.id)
                          .collection("messages")
                          .orderBy("timestamp", "desc")
                          .onSnapshot((snapshot) => setMessages(
                              snapshot.docs.map((doc) => ({
                                 id: doc.id,
                                 data: doc.data()
                              }))
                          ));
                          return unsubscribe;
    },[route])

    return (
        <SafeAreaView style={{flex: 1, backgroundColor: "white"}}>
            <StatusBar style="light" />
            <KeyboardAvoidingView
              behavior={Platform.OS === "android" ? "padding":"height"}
              keyboardVerticalOffset={-500}
              style={styles.container}>
                  <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
                  <>
                <ScrollView>
                     {messages.map(({id,data}) => 
                         data.email === auth.currentUser.email ? (
                             <View key={id} style={styles.reciever}>
                                 <Avatar rounded 
                                         size={30} 
                                         bottom={-15} 
                                         right={-5} 
                                         position="absolute"
                                         containerStyle={{
                                             position: "absolute",
                                             bottom: -15,
                                             right: -5
                                         }}
                                         source={{uri: data.photoURL}}/>
                                 <Text style={styles.recieverText}>{data.message}</Text>
                             </View>
                         ): 
                         (
                             <View key={id} style={styles.sender}>
                                 <Avatar 
                                 rounded 
                                 size={30} 
                                 bottom={-15} 
                                 right={-5} 
                                 position="absolute"
                                 containerStyle={{
                                     position: "absolute",
                                     bottom: -15,
                                     right: -5
                                 }}
                                 source={{uri: data.photoURL}}/>
                                 <Text style={styles.senderText}>{data.message}</Text>
                                 <Text style={styles.senderName}>{data.displayName}</Text>
                             </View>
                         )
                     )}
                </ScrollView>
                <View style={styles.footer}>
                    <TextInput placeholder="Enter Message" 
                               value={input}  
                               style={styles.textInput} 
                               onChangeText={(text) => setInput(text)}
                               onSubmitEditing={sendMessage}/>
                    <TouchableOpacity disabled={!input} activeOpacity={0.5} onPress={sendMessage}>
                               <Ionicons name="send" size={24} color="#2B68E6"/>
                    </TouchableOpacity>           
                </View>
                </>
                </TouchableWithoutFeedback>
              </KeyboardAvoidingView>
        </SafeAreaView>
    )
}

export default ChatScreen

const styles = StyleSheet.create({
    container: {
      flex: 1,
      paddingBottom: -20
    },
    reciever: {
      padding: 15,
      backgroundColor: "#ECECEC",
      alignSelf: "flex-end",
      borderRadius: 20,
      marginRight: 15,
      marginBottom: 20,
      maxWidth: "80%",
      position: "relative"
    },
    sender: {
      padding: 15,
      backgroundColor: "#0000FF",
      alignSelf: "flex-start",
      borderRadius: 20,
      margin: 15,
      maxWidth: "80%",
      position: "relative"
    },
    senderText: {
      color: "white",
      fontWeight: "500",
      marginLeft: 10,
      marginBottom: 15
    },
    recieverText: {
      color: "black",
      marginLeft: 10,
      fontWeight: "500"
    },  
    senderName: {
        left: 10,
        paddingRight: 10,
        fontSize: 10,
        color: "white"
    },
    footer: {
      flexDirection: "row",
      alignItems: "center",
      width: "100%",
      padding: 10
        },
    textInput: {
      bottom: 0,
      height: 40,
      flex: 1,
      marginRight: 15,
      backgroundColor: "#ECECEC",
      padding: 10,
      color: "grey",
      borderRadius: 30
    }
})
